require "config"local game_speed = settings.startup["Proxy_Wars_game_speed"].value--1 would do nothing--Less than 1 would increase game lengthif game_speed ~= 1 then	for _, recipe in pairs(data.raw.recipe) do		--Adjust the energy		if recipe.energy_required then			recipe.energy_required = recipe.energy_required/game_speed		end				if recipe.ingredients then			--Adjust the ingredients			for _, ingredient in pairs(recipe.ingredients) do				if ingredient.amount then					ingredient.amount = math.ceil(ingredient.amount/game_speed)				else					ingredient[2] = math.ceil(ingredient[2]/game_speed)				end			end		else			--If it doesn't have ingredients, then it has normal and expensive ingredients			for _, ingredient in pairs(recipe.normal.ingredients) do				if ingredient.amount then					ingredient.amount = math.ceil(ingredient.amount/game_speed)				else					ingredient[2] = math.ceil(ingredient[2]/game_speed)				end			end			for _, ingredient in pairs(recipe.expensive.ingredients) do				if ingredient.amount then					ingredient.amount = math.ceil(ingredient.amount/game_speed)				else					ingredient[2] = math.ceil(ingredient[2]/game_speed)				end			end		end	endend