require "config"--1 would do nothing--Less than 1 would increase game lengthif game_speed > 0 and game_speed ~= 1 then	for _, recipe in pairs(data.raw.recipe) do		--Adjust the energy		if recipe.energy_required then			local newEnergy = recipe.energy_required/game_speed			if newEnergy < minimum_base_crafting_speed then				newEnergy = minimum_base_crafting_speed			end			recipe.energy_required = newEnergy		end				--Adjust the ingredients		for _, ingredient in pairs(recipe.ingredients) do			if ingredient.amount then				ingredient.amount = math.ceil(ingredient.amount/game_speed)			else				ingredient[2] = math.ceil(ingredient[2]/game_speed)			end		end	endend